import Header from "../../components/header/Header";
import "./Routines.css";
import routines from "../../helpers/data";
import { Link } from "react-router-dom";
import React, { useEffect } from 'react';

const AutoRefreshPage: React.FC = () => {
    useEffect(() => {
      const refreshInterval = setInterval(() => {
        window.location.reload();
      }, 30000); // Actualiza la página cada 30 segundos (ajusta el valor según tus necesidades)
  
      return () => {
        clearInterval(refreshInterval);
      };
    }, []);
}

function Routines() {
    return (
        <>
            <header><Header /></header>
            <body>
                <div className="container">
                    {routines.map((routine, idx) => (
                        <Link to={`/routinedays/${routine.title}`} key={idx}>{routine.title}</Link>
                    ))}
                </div>
            </body>
        </>
    );
}

export default Routines;
