import { Link, useParams } from 'react-router-dom';
import data from '../../helpers/data';
import { useState } from 'react'
import Header from '../../components/header/Header';

const RoutineDays = () => {
    const { id } = useParams();
    const routine = data.find(routine => routine.id === id);

    const [contentMenu, setContentMenu] = useState(false)

    const toggleRoutinesMenu = () => {
        setContentMenu(!contentMenu)
    }

    return (
        <>
            <div className='containerRoutine'>
                <div className='returnHome'>
                    <Header />
                </div>
                <div className='title'>
                    <h1>{routine?.title} ROUTINES</h1>
                </div>
                <div className='content'>
                    {routine?.routines.map((routine, index) => (
                        <div key={index}>
                            <h2>{routine.name}</h2>
                            {routine.days.map((day, index) => (
                                <div key={index}>
                                    <h3>{day.firstDay.day}</h3>
                                    {day.firstDay.exercises.map((exercise, index) => (
                                        <ul key={index}>
                                            <li>{exercise.step1}</li>
                                            <li>{exercise.step2}</li>
                                            <li>{exercise.step3}</li>
                                            <li>{exercise.step4}</li>
                                            <li>{exercise.step5}</li>
                                            <li>{exercise.step6}</li>
                                            <li>{exercise.step7}</li>
                                        </ul>
                                    ))}
                                </div>
                            ))}
                        </div>
                    ))}
                    <p></p>
                    <nav className={`content-nav ${contentMenu ? 'isActive' : ''}`}>
                        <ul>
                            <li></li>
                        </ul>
                    </nav>
                </div>
            </div>
        </>
    )

};

export default RoutineDays;
