import { Link, useParams } from 'react-router-dom';
import exercises from '../../helpers/exercises';
import { useState } from 'react'
import Header from '../../components/header/Header';
import { DownOutlined } from '@ant-design/icons';
import "./Exercises.css"

const Exercises = () => {

    const { id } = useParams();
    const exercise = exercises.find(exercise => exercise.id === id);
    const [contentMenu, setContentMenu] = useState(false)
    const toggleRoutinesMenu = () => {
        setContentMenu(!contentMenu)
    }

    return (
        <>
            <div className='containerRoutine'>
                <div className='returnHome'>
                    <Header />
                </div>
                <div className='title'>
                    <h1>{exercise?.title}</h1>
                </div>
                <div className='content'>
                    <nav className={`content-nav ${contentMenu ? 'isActive' : ''}`}>
                        {Object.entries(routine).map(([key, exercise], index) => (
                            key.startsWith('exercise') && (
                                <div key={index}>
                                    <div className='routineNameContent'>
                                        <h2 className='routineName'>
                                            {exercise.name}
                                            <DownOutlined onClick={toggleRoutinesMenu} className='routinesButton' />
                                        </h2>
                                    </div>
                                    <div>
                                        <img src={exercise.img} alt={exercise.name} />
                                        <a href={exercise.link}>Ver tutorial</a>
                                    </div>
                                </div>
                            )
                        ))}
                    </nav>
                </div>
            </div>
        </>
    )
}

export default Exercises
